import React, { Component } from 'react';
import { Button, Jumbotron, Container, Toast} from 'react-bootstrap';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      newItem:"",
      list:[]
    }

  }

  updateInput(key, value){
    this.setState({
      [key]: value
    });
  }
  addItem(){
    //create item with unique id
    const newItem={
      id: 1 + Math.random(),
      value: this.state.newItem.slice()
    };
    //copy of current list of items
    const list = [...this.state.list];

    //add new item to list
    list.push(newItem);

    //update state with new list and reset newItem input
    this.setState({
      list,
      newItem:""
    })
  }

  deleteItem(id){
    //copy current list of items
    const list = [...this.state.list];

    //filter out item being deleted
    const updatedList = list.filter(item => item.id !== id);
    this.setState({
      list: updatedList
    });
  }

  render(){
    return (
      <Container className="App">
         <Jumbotron>
            <h1>App</h1>
            <p>
              Todolist- Add an item!
            </p>
            <p>
            <input
              type = "text"
              placeholder="TYpe the text here"
              value = {this.state.newItem}
              onChange={e => this.updateInput("newItem", e.target.value)}
              />
              </p>
            <p>
              <Button variant="primary" onClick={() => this.addItem()}>Add</Button>
            </p>
            <p className="Dot">
            {this.state.list.map(item => {
              return (
                <p key={item.id}>
                  {item.value}
                  <Button className="Buttonname" onClick={() => this.deleteItem(item.id)} variant="primary">
                    x
                  </Button>
                </p>
              )
            })} 
            </p>
            {!show && <Button onClick={() => toggleShow(true)}>Show Toast</Button>}
              <Toast show={show} onClose={() => toggleShow(false)}>
                <Toast.Header>
                  <strong className="mr-auto">React-Bootstrap</strong>
                </Toast.Header>
                <Toast.Body>{children}</Toast.Body>
              </Toast>
          </Jumbotron>
        </Container>
    );
  }
}

export default App;
